import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import LOGO from "./assets/logo192.jpeg";
import moment from "moment";
import axios from "axios";
import Backdrop from "@material-ui/core/Backdrop";
import CircularProgress from "@material-ui/core/CircularProgress";
import Banner from "./assets/uNGdWHi.png";
import { accesstoken } from "./Config";

import {
  Navbar,
  Nav,
  Container,
  NavDropdown,
  InputGroup,
  FormControl,
  Table
} from "react-bootstrap";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      y: moment(new Date()).format("YYYY"),
      email: "",
      backdop: false,
      usersError: false,
      dateNow: new Date(),
    };

    setInterval(() => this.chTime(), 1000);
  }

  componentDidMount() {
    accesstoken().then((result) => {
      if (result == undefined) {
        this.props.history.push("/");
      }
    });
  }

  chTime() {
    //console.log('ChTime');
    this.setState({ dateNow: new Date() });
  }

  checklogin = async () => {
    this.setState({
      backdop: true,
      usersError: false,
    });
    const res = await axios.get(
      "https://script.google.com/macros/s/AKfycbyJfsN-HPCrpJhDl6Q7m0ZtF_ehYqtWIYPwmTtv7OYVqhxEoX2HqUU5G2uHneTICH3l/exec?action=read&table=cheque&email="
    );
  };
  render() {
    return (
      <div>
        <Navbar collapseOnSelect expand="lg" bg="secondary" variant="dark">
          <Container>
            <Navbar.Brand>ระบบตรวจสอบสถานะการจ่ายเช็ค</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="me-auto">
                <NavDropdown title="เมนูเพิ่มเติม" id="basic-nav-dropdown">
                  <NavDropdown.Item href="#action/3.1">
                    แสดงรายการทั้งหมด
                  </NavDropdown.Item>
                  <NavDropdown.Item href="#action/3.1">
                    แสดงรายการเฉพาะเช็คที่เตรียมจ่าย
                  </NavDropdown.Item>
                  <NavDropdown.Divider />
                  <NavDropdown.Item href="#action/3.4">
                    ออกจากระบบ
                  </NavDropdown.Item>
                </NavDropdown>
              </Nav>
            </Navbar.Collapse>
          </Container>
        </Navbar>
        <main role="main" className="container">
          <div
            className="d-flex align-items-center p-3 my-3 text-white-50 bg-purple rounded box-shadow"
            style={{ backgroundColor: "#512da8" }}
          >
            <img
              className="mr-3"
              src="https://getbootstrap.com/docs/4.0/assets/brand/bootstrap-outline.svg"
              alt=""
              width={48}
              height={48}
            />
            <div className="lh-100">
              <h6 className="mb-0 text-white lh-100">
                องค์การบริหารส่วนตำบล แก่งโสภา อ.วังทอง จ.พิษณุโลก 65000
              </h6>
              <small>
                {this.state.dateNow.toLocaleDateString() + " "}{" "}
                {this.state.dateNow.toLocaleTimeString()}
              </small>
            </div>
          </div>
          <div className="my-3 p-3 bg-white rounded box-shadow">
            <h6 className="border-gray pb-2 mb-0">แสดงรายการ</h6>
            <InputGroup className="mb-3">
              <FormControl
                placeholder="ค้นหาข้อมูลจากรายการด้านล่าง"
                aria-label="ค้นหาข้อมูลจากรายการด้านล่าง"
                aria-describedby="basic-addon1"
              />
            </InputGroup>
            <div className="media text-muted pt-3">
              <Table striped bordered hover size="sm">
                <thead>
                  <tr>
                    <th>#</th>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Username</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>1</td>
                    <td>Mark</td>
                    <td>Otto</td>
                    <td>@mdo</td>
                  </tr>

                </tbody>
              </Table>
            </div>
          </div>
        </main>
      </div>
    );
  }
}

export default App;
